/**
 * Task Details
 */
export interface Task {
    /** Companies ids for companies a task is linked to */
    companiesIds?: string[];
    /** Contact ids for contacts linked to this task */
    contactsIds?: number[];
    /** Deal ids for deals a task is linked to */
    dealsIds?: string[];
    /** Unique task id */
    id?: string;
    /** Name of task */
    name: string;
    /** Id for type of task e.g Call / Email / Meeting etc. */
    taskTypeId: string;
}
